// Processar cria√ß√£o de evento
const evento = $input.first().json;

// Calcular dura√ß√£o
const inicio = new Date(evento.fields.data_inicio);
const fim = new Date(evento.fields.data_fim);
const duracaoMinutos = Math.round((fim - inicio) / (1000 * 60));

const resposta = {
  sucesso: true,
  mensagem: `üìÖ *Evento criado com sucesso!*\n\n` +
            `üéØ *${evento.fields.titulo}*\n` +
            `üìÖ Data: ${inicio.toLocaleDateString('pt-BR')}\n` +
            `‚è∞ Hor√°rio: ${inicio.toLocaleTimeString('pt-BR', {hour: '2-digit', minute: '2-digit'})} - ${fim.toLocaleTimeString('pt-BR', {hour: '2-digit', minute: '2-digit'})}\n` +
            `‚è±Ô∏è Dura√ß√£o: ${Math.floor(duracaoMinutos/60)}h${duracaoMinutos%60 > 0 ? duracaoMinutos%60 + 'm' : ''}\n` +
            `üìç Local: ${evento.fields.local || 'N√£o definido'}\n` +
            `üîî Lembrete: ${evento.fields.lembrete_minutos} min antes\n\n` +
            `ID: ${evento.id}`,
  evento_id: evento.id,
  dados: evento.fields,
  google_sync: false // Flag para sincroniza√ß√£o futura
};

return [{ json: resposta }];

// Processar listagem geral de eventos
const eventos = $input.first().json.records || [];

if (eventos.length === 0) {
  return [{
    json: {
      sucesso: true,
      mensagem: `üìÖ *Sua Agenda*\n\n` +
                `üì≠ Voc√™ n√£o tem eventos agendados.\n\n` +
                `Use */agenda criar [evento]* para adicionar um novo evento.`,
      total: 0
    }
  }];
}

// Agrupar eventos por data
const eventosPorData = {};
eventos.forEach(evento => {
  const data = new Date(evento.fields.data_inicio).toLocaleDateString('pt-BR');
  if (!eventosPorData[data]) {
    eventosPorData[data] = [];
  }
  eventosPorData[data].push(evento);
});

let mensagem = `üìÖ *Sua Agenda* (${eventos.length} eventos)\n\n`;

Object.keys(eventosPorData).sort().forEach(data => {
  mensagem += `üìÖ *${data}*\n`;

  eventosPorData[data].forEach(evento => {
    const inicio = new Date(evento.fields.data_inicio);
    const horario = inicio.toLocaleTimeString('pt-BR', {hour: '2-digit', minute: '2-digit'});

    const tipoIcon = {
      'reuniao': 'üë•',
      'tarefa': 'üìã',
      'compromisso': 'üìç',
      'lembrete': 'üîî',
      'bloqueio': 'üö´'
    }[evento.fields.tipo] || 'üìÖ';

    const statusIcon = {
      'agendado': '‚è≥',
      'confirmado': '‚úÖ',
      'cancelado': '‚ùå',
      'concluido': '‚úÖ'
    }[evento.fields.status] || '‚ùì';

    mensagem += `  ${tipoIcon} ${statusIcon} ${horario} - ${evento.fields.titulo}\n`;

    if (evento.fields.local) {
      mensagem += `    üìç ${evento.fields.local}\n`;
    }
  });

  mensagem += `\n`;
});

mensagem += `üí° Use */agenda hoje* para ver apenas os eventos de hoje.`;

return [{
  json: {
    sucesso: true,
    mensagem: mensagem,
    total: eventos.length,
    eventos: eventos
  }
}];

// Processar eventos de hoje
const eventos = $input.first().json.records || [];
const hoje = new Date().toLocaleDateString('pt-BR');

if (eventos.length === 0) {
  return [{
    json: {
      sucesso: true,
      mensagem: `üìÖ *Agenda de Hoje* - ${hoje}\n\n` +
                `üéâ Voc√™ n√£o tem eventos agendados para hoje!\n\n` +
                `Aproveite para focar em suas tarefas ou relaxar. üòä\n\n` +
                `Use */agenda criar* para adicionar um evento.`,
      total: 0
    }
  }];
}

let mensagem = `üìÖ *Agenda de Hoje* - ${hoje}\n\n`;

// Ordenar eventos por hor√°rio
eventos.sort((a, b) => new Date(a.fields.data_inicio) - new Date(b.fields.data_inicio));

eventos.forEach((evento, index) => {
  const inicio = new Date(evento.fields.data_inicio);
  const fim = new Date(evento.fields.data_fim);
  const horarioInicio = inicio.toLocaleTimeString('pt-BR', {hour: '2-digit', minute: '2-digit'});
  const horarioFim = fim.toLocaleTimeString('pt-BR', {hour: '2-digit', minute: '2-digit'});

  const tipoIcon = {
    'reuniao': 'üë•',
    'tarefa': 'üìã',
    'compromisso': 'üìç',
    'lembrete': 'üîî',
    'bloqueio': 'üö´'
  }[evento.fields.tipo] || 'üìÖ';

  // Verificar se o evento est√° pr√≥ximo (pr√≥ximas 2 horas)
  const agora = new Date();
  const diffHoras = (inicio - agora) / (1000 * 60 * 60);
  let proximidadeIcon = '';

  if (diffHoras <= 0.5 && diffHoras > -1) {
    proximidadeIcon = 'üî¥ ';
  } else if (diffHoras <= 2) {
    proximidadeIcon = 'üü° ';
  }

  mensagem += `${proximidadeIcon}${tipoIcon} *${horarioInicio} - ${horarioFim}*\n`;
  mensagem += `   ${evento.fields.titulo}\n`;

  if (evento.fields.local) {
    mensagem += `   üìç ${evento.fields.local}\n`;
  }

  if (evento.fields.participantes) {
    mensagem += `   üë• ${evento.fields.participantes}\n`;
  }

  if (evento.fields.link_reuniao) {
    mensagem += `   üîó Link da reuni√£o dispon√≠vel\n`;
  }

  mensagem += `\n`;
});

// Adicionar dicas baseadas no hor√°rio
const horaAtual = new Date().getHours();
if (horaAtual < 9) {
  mensagem += `üåÖ *Bom dia!* Prepare-se para um dia produtivo!`;
} else if (horaAtual < 12) {
  mensagem += `‚òÄÔ∏è *Bom dia!* Manh√£ ideal para foco e concentra√ß√£o!`;
} else if (horaAtual < 18) {
  mensagem += `üåû *Boa tarde!* Continue firme com seus compromissos!`;
} else {
  mensagem += `üåô *Boa noite!* Finalize bem o seu dia!`;
}

return [{
  json: {
    sucesso: true,
    mensagem: mensagem,
    total: eventos.length,
    eventos: eventos
  }
}];

// Processar eventos da semana
const eventos = $input.first().json.records || [];

if (eventos.length === 0) {
  return [{
    json: {
      sucesso: true,
      mensagem: `üìÖ *Agenda da Semana*\n\n` +
                `üì≠ Voc√™ n√£o tem eventos agendados para esta semana.\n\n` +
                `Uma semana mais tranquila para focar em projetos! üéØ`,
      total: 0
    }
  }];
}

// Agrupar eventos por dia da semana
const diasSemana = ['Domingo', 'Segunda', 'Ter√ßa', 'Quarta', 'Quinta', 'Sexta', 'S√°bado'];
const eventosPorDia = {};

eventos.forEach(evento => {
  const data = new Date(evento.fields.data_inicio);
  const diaSemana = diasSemana[data.getDay()];
  const dataFormatada = data.toLocaleDateString('pt-BR');
  const chave = `${diaSemana} (${dataFormatada})`;

  if (!eventosPorDia[chave]) {
    eventosPorDia[chave] = [];
  }
  eventosPorDia[chave].push(evento);
});

let mensagem = `üìÖ *Agenda da Semana* (${eventos.length} eventos)\n\n`;

// Ordenar dias da semana
const diasOrdenados = Object.keys(eventosPorDia).sort((a, b) => {
  const dataA = new Date(a.split('(')[1].split(')')[0].split('/').reverse().join('-'));
  const dataB = new Date(b.split('(')[1].split(')')[0].split('/').reverse().join('-'));
  return dataA - dataB;
});

dias Ordenados.forEach(dia => {
  const eventosNoDia = eventosPorDia[dia];
  mensagem += `üìÖ *${dia}* - ${eventosNoDia.length} evento(s)\n`;

  // Ordenar eventos do dia por hor√°rio
  eventosNoDia.sort((a, b) => new Date(a.fields.data_inicio) - new Date(b.fields.data_inicio));

  eventosNoDia.forEach(evento => {
    const inicio = new Date(evento.fields.data_inicio);
    const horario = inicio.toLocaleTimeString('pt-BR', {hour: '2-digit', minute: '2-digit'});

    const tipoIcon = {
      'reuniao': 'üë•',
      'tarefa': 'üìã',
      'compromisso': 'üìç',
      'lembrete': 'üîî',
      'bloqueio': 'üö´'
    }[evento.fields.tipo] || 'üìÖ';

    mensagem += `  ${tipoIcon} ${horario} - ${evento.fields.titulo}\n`;
  });

  mensagem += `\n`;
});

// Estat√≠sticas da semana
const tiposEventos = {};
eventos.forEach(evento => {
  const tipo = evento.fields.tipo;
  tiposEventos[tipo] = (tiposEventos[tipo] || 0) + 1;
});

mensagem += `üìä *Resumo da Semana:*\n`;
Object.keys(tiposEventos).forEach(tipo => {
  const icon = {
    'reuniao': 'üë•',
    'tarefa': 'üìã',
    'compromisso': 'üìç',
    'lembrete': 'üîî',
    'bloqueio': 'üö´'
  }[tipo] || 'üìÖ';

  mensagem += `${icon} ${tipo}: ${tiposEventos[tipo]}\n`;
});

return [{
  json: {
    sucesso: true,
    mensagem: mensagem,
    total: eventos.length,
    eventos: eventos,
    estatisticas: tiposEventos
  }
}];

// Processar atualiza√ß√£o de evento
const evento = $input.first().json;

const resposta = {
  sucesso: true,
  mensagem: `üîÑ *Evento Atualizado!*\n\n` +
            `üìÖ ${evento.fields.titulo}\n` +
            `üìÖ Data: ${new Date(evento.fields.data_inicio).toLocaleDateString('pt-BR')}\n` +
            `‚è∞ Hor√°rio: ${new Date(evento.fields.data_inicio).toLocaleTimeString('pt-BR', {hour: '2-digit', minute: '2-digit'})}\n` +
            `üìç Status: ${evento.fields.status}\n\n` +
            `‚ú® Altera√ß√µes salvas com sucesso!`,
  evento_id: evento.id
};

return [{ json: resposta }];

// Processar cancelamento de evento
const resposta = {
  sucesso: true,
  mensagem: `‚ùå *Evento Cancelado*\n\n` +
            `O evento foi cancelado com sucesso.\n\n` +
            `üìß Participantes ser√£o notificados automaticamente.`,
  evento_id: $input.first().json.evento_id
};

return [{ json: resposta }];

// Preparar dados para sincroniza√ß√£o com Google Calendar
const evento = $input.first().json;

// Converter para formato Google Calendar
const googleEvent = {
  summary: evento.dados.titulo,
  description: evento.dados.descricao,
  start: {
    dateTime: evento.dados.data_inicio,
    timeZone: 'America/Sao_Paulo'
  },
  end: {
    dateTime: evento.dados.data_fim,
    timeZone: 'America/Sao_Paulo'
  },
  location: evento.dados.local,
  reminders: {
    useDefault: false,
    overrides: [
      {
        method: 'popup',
        minutes: evento.dados.lembrete_minutos
      }
    ]
  }
};

if (evento.dados.recorrencia !== 'nenhuma') {
  // Configurar recorr√™ncia
  const recurrenceRules = {
    'diaria': 'RRULE:FREQ=DAILY',
    'semanal': 'RRULE:FREQ=WEEKLY',
    'mensal': 'RRULE:FREQ=MONTHLY',
    'anual': 'RRULE:FREQ=YEARLY'
  };

  googleEvent.recurrence = [recurrenceRules[evento.dados.recorrencia]];
}

return [{
  json: {
    ...evento,
    google_event: googleEvent,
    sync_required: true
  }
}];
